DROP TABLE IF EXISTS `custom_npc`;
CREATE TABLE `custom_npc` (
  `id` mediumint(7) unsigned NOT NULL,
  `idTemplate` smallint(5) unsigned NOT NULL,
  `name` varchar(200) NOT NULL DEFAULT '',
